import { createApp } from 'vue';
import App from './App.vue';
import { createRouter, createWebHistory } from 'vue-router';
import { createAuth0 } from '@auth0/auth0-vue';

import HomePage from './components/HomePage.vue';
import LoginPage from './components/LoginPage.vue';
import RegisEquipamento from './components/RegistrarEquipamento.vue';
import Inicio from './components/PaginaInicio.vue';
import CriarConta from './components/CriarConta.vue';
import ADDCatalogo from './components/AddCatalogo.vue';
import InfoUser from './components/InfoUser.vue';
import PaginaProduto from './components/PaginaProduto.vue';


import axios from 'axios';
// Configuração das rotas
const router = createRouter({
  history: createWebHistory(),
  routes: [
    { path: '/', component: Inicio },
  { path: '/login', component: LoginPage },
  { path: '/criar_conta', component: CriarConta },
  { path: '/home', component: HomePage, meta: { requiresAuth: true } },
  { path: '/registroEquipamento', component: RegisEquipamento, meta: { requiresAuth: true } },
  { path: '/addToCatalog', component: ADDCatalogo, meta: { requiresAuth: true } },
  { path: '/infoUtilizador', component: InfoUser, meta: { requiresAuth: true } },
  { path: '/produto/:id', name: 'DetalhesProduto', component: PaginaProduto }


  ],
});

import { authGuard } from '@auth0/auth0-vue';

router.beforeEach(authGuard);



// Criando a instância do Vue
const app = createApp(App);

app.use(
  createAuth0({
    domain: 'dev-5s50cdhbvpkv05gq.us.auth0.com',
    clientId: 'zGSIW7Cd7aC40F32g2bFsvdG9jJAK53W',
    authorizationParams: {
      redirect_uri: window.location.origin
    }
  })
);
app.use(router); // Registrando o Vue Router
app.mount('#app'); // Montando o app

axios.defaults.baseURL = 'http://localhost:3000/api';

import { useAuth0 } from '@auth0/auth0-vue';

axios.interceptors.request.use(async config => {
  const auth0 = useAuth0(); // isso só funciona dentro de componentes Vue!
  const token = await auth0.getAccessTokenSilently().catch(() => null);

  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }

  return config;
});
