import { defineStore } from 'pinia';
import { ref } from 'vue';

export const useCarrinhoStore = defineStore('carrinho', () => {
  const equipamentos = ref([]);

  function adicionarAoCarrinho(produto) {
  const existente = this.equipamentos.find(p => p.id === produto.id);
  if (existente) {
    existente.quantidade += 1;
  } else {
    this.equipamentos.push({ ...produto, quantidade: 1 });
  }
  this.salvarCarrinho();
}


  function contar() {
    return equipamentos.value.length;
  }

  return {
    equipamentos,
    adicionarAoCarrinho,
    contar
  };
});
